package com.example.backend_login_demo;import org.springframework.data.jpa.repository.JpaRepository;import java.util.Optional;// Spring Data JPA에서 제공하는 JpaRepository 인터페이스를 확장하여 사용자 정의 repository를 만드는 것// PersonRepository는 JpaRepository에서 제공하는 기본적인 CRUD 메서드를 상속 받음// findByUserId()를 정의: userId 필드를 사용하여 Person 엔티티를 찾음.// Optional을 사용하면 결과가 없을 경우 null 대신에 Optional.empty()를 반환. NPE 등의 오류가 발생하지 않음.public interface PersonRepository extends JpaRepository<Person, Integer> {    Optional<Person> findByUserId(String userId);}